import React, { Component } from 'react';
import ListGroup from './common/listGroup';
import raw from '../data/page1.txt'

class Movies extends Component {
    state = {
        movies: [
            { title: 'readme.txt', genre: 'aaaaaaaaaaaaaa' },
            { title: 'page1.jsx', genre: 'bbbbbbbbbbbbbb' },
            { title: 'page2.jsx', genre: 'ccccccccccccc' },
            { title: 'page3.jsx', genre: 'dddddddddddd' },
            { title: 'page4.jsx', genre: 'eeeeeeeeeeeee' },
        ],
        selection: "",
        code: ""
    }

    handleMovieSelect = (selection) => {

        let promise = fetch(raw)
            .then(r => r.text())
            .then(text => {
                console.log('text decoded:', text);
                this.setState({ movies: this.state.movies, selection: selection, code: text })
            });

    }

    render() {
        const currentPage = this.state.movies.find(element => {
            return element.title === this.state.selection;
        })



        return (
            <React.Fragment>
                <div style={{ marginTop: 50 }} />

                <div className="row">

                    <div className="col-2">
                        <ListGroup items={this.state.movies} onItemSelect={(selection) => this.handleMovieSelect(selection)} />
                    </div>

                    <div className="col">
                        <h1>{currentPage.genre}</h1>
                        <h3>{this.state.code}</h3>

                    </div>


                </div>


            </React.Fragment>



        );
    }
}

export default Movies;
